mapper.select:
SELECT schedule_no, trainer_no AS `trainer.trainer_no`, schedule_date, schedule_from, schedule_to, schedule_msg FROM schedule
JOIN trainer using (trainer_no)
WHERE 1=1
AND trainer_no = #{trainer.trainer_no}
AND schedule_date = #{schedule_date}

mapper.delete:
DELETE FROM pt
WHERE 1=1
AND user_no = #{user_no}
AND pt_date = #{pt_date}

DELETE FROM schedule
WHERE 1=1
AND trainer_no = #{trainer_no}
AND schedule_date = #{schedule_date}
AND schedule_from = #{schedule_from}

mapper.select:
SELECT schedule_no, pt_type_no, user_no AS `user.user_no`, pt_request_date, pt_date FROM pt
WHERE 1=1
AND user_no = #{user_no}
AND pt_date BETWEEN #{start} AND #{end}
ORDER BY pt_date
